window.nextSlide=()=>{const e=document.querySelector(".slide.active");e.nextElementSibling&&e.nextElementSibling.classList.contains("slide")&&(e.classList.remove("active"),e.nextElementSibling.classList.add("active"))};window.prevSlide=()=>{const e=document.querySelector(".slide.active");e.previousElementSibling&&(e.classList.remove("active"),e.previousElementSibling.classList.add("active"))};document.querySelectorAll(".slide").forEach(e=>{e.addEventListener("click",()=>{nextSlide()})});document.addEventListener("keydown",e=>{e.key==="ArrowRight"&&nextSlide(),e.key==="ArrowLeft"&&prevSlide()});const u="ABCDEFGHIJKLMNOPQRSTUVWXYZ123456789".split("");window.addWinner=(e,s,i=2e3,c=2e3)=>{const[m,o]=e[0],l=document.querySelector(s),r=l.querySelectorAll(".final__code-item"),d=[];r.forEach(n=>{d.push(setInterval(()=>{n.innerHTML=u[Math.floor(Math.random()*u.length)]},100))}),setTimeout(()=>{const n=m.split("");d.forEach((t,v)=>{clearInterval(t)}),r.forEach((t,v)=>{t.innerHTML=n[v]});const a=l.querySelector(".final-last-winner");a&&(a.textContent=o);const S=l.querySelector(".winners__list");if(S){const t=document.createElement("div");t.classList.add("winner"),t.textContent=o,S.appendChild(t)}},i),setTimeout(()=>{const n=e.slice(1);n.length>0&&addWinner(n,s,i,c)},i+c)};
